/*

TODO:

- raycaster to show image description -> perfect it later

then

- include in claudia's website
- make sure fog is adjusted to bg
- maybe canvas size should be smaller, like width:90%
- make a preloader of all images

*/ 

import "./main.css"
import * as THREE from "three";
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls.js';
import EffectComposer, {
  RenderPass,
  ShaderPass,
} from '@johh/three-effectcomposer';

let camera, scene, renderer, controls;
let imgArray = [];
const objects = [];
let composer, renderPass, customPass;
let uMouse = new THREE.Vector2();
// let raycaster = new THREE.Raycaster();
// let mouse = new THREE.Vector2();

let myEffect = {
  uniforms: {
    "tDiffuse": { value: null },
    "resolution": { value: new THREE.Vector2(1.,window.innerHeight/window.innerWidth) },
    "uMouse": { value: new THREE.Vector2(-1,-1) },
    "uVelo": { value: 0 },
  },
  vertexShader: `varying vec2 vUv;void main() {vUv = uv;gl_Position = projectionMatrix * modelViewMatrix * vec4(position, 1.0 );}`,
  fragmentShader: `uniform float time;
  uniform sampler2D tDiffuse;
  uniform vec2 resolution;
  varying vec2 vUv;
  uniform vec2 uMouse;
  float circle(vec2 uv, vec2 disc_center, float disc_radius, float border_size) {
    uv -= disc_center;
    uv*=resolution;
    float dist = sqrt(dot(uv, uv));
    return smoothstep(disc_radius+border_size, disc_radius-border_size, dist);
  }
  void main()  {
      vec2 newUV = vUv;
      float c = circle(vUv, uMouse, 0.0, 0.2);
      float r = texture2D(tDiffuse, newUV.xy += c * (0.1 * .5)).x;
      float g = texture2D(tDiffuse, newUV.xy += c * (0.1 * .525)).y;
      float b = texture2D(tDiffuse, newUV.xy += c * (0.1 * .55)).z;
      vec4 color = vec4(r, g, b, 1.);

      gl_FragColor = color;
  }`
}

init();
animate();

function init() {
  camera = new THREE.PerspectiveCamera(
    45, window.innerWidth / window.innerHeight, 0.1, 2000
  );

  /*sides, upndown, frontnback */
  camera.position.set(0, 0, 5);

  scene = new THREE.Scene();
  camera.lookAt(scene.position);
  

  //const material = new THREE.MeshBasicMaterial({color:0x000000,side:THREE.DoubleSide});
  const count = 27;

  // for each img in folder x do...
  for (let i = 0; i < count; i++) {

    // load texture
    const texture = new THREE.TextureLoader().load( 'http://localhost:8000/img/opf/opf-'+i+'.jpg' );
    
    texture.minFilter = THREE.LinearFilter;
    imgArray.push(new Image());

    imgArray[i].src = 'http://localhost:8000/img/opf/opf-'+i+'.jpg';
    
    const mesh = new THREE.Mesh(new THREE.PlaneBufferGeometry(1, 1), new THREE.MeshBasicMaterial({map:texture, side:THREE.DoubleSide}));
    
    imgArray[i].onload = function(){

      // adjust these consitions to you image dimensions
      if(imgArray[i].width > 1200) {
        mesh.scale.x = imgArray[i].width/4000;
        mesh.scale.y = imgArray[i].height/4000;  
      } else {
        mesh.scale.x = imgArray[i].width/700;
        mesh.scale.y = imgArray[i].height/700;
      }
    }

    const t = (i / count) * 2 * Math.PI + 2; //+2 is a shift right

    mesh.position.x = Math.cos(t) * 4;

    mesh.position.z = Math.sin(t) * 4;
    
    // experiment with the following for some fun results
    //
    // mesh.position.x = Math.cos(t) * 10;
    // mesh.position.z = Math.sin(t) * 4;

    scene.add(mesh);
    objects.push(mesh);
    
  }

  
  //scene.fog = new THREE.Fog( 0xffffff, 1, 1.3);

  renderer = new THREE.WebGLRenderer({ alpha: true});
  renderer.setPixelRatio(window.devicePixelRatio);
  renderer.setSize(window.innerWidth, window.innerHeight);
  renderer.setClearColor( 0xffffff, 0);
  document.body.appendChild(renderer.domElement);
  
  

   // Post Processing
  composer = new EffectComposer(renderer);
  renderPass = new RenderPass(scene, camera);
  //renderPass.renderToScreen = true;
  //renderPass.material.transparent = true;
  composer.addPass(renderPass);
  customPass = new ShaderPass(myEffect);
  //customPass.uniforms.resolution.value.set( 1 / window.innerWidth, 1 / window.innerHeight );
  customPass.renderToScreen = true;
  customPass.material.transparent = true;

  composer.addPass(customPass);
  
  //composer.setClearColor( 0xffffff, 0);

  controls = new OrbitControls(camera, renderer.domElement);
  controls.minPolarAngle = Math.PI/2;

  controls.maxPolarAngle = Math.PI/2;
  controls.rotateSpeed = 0.05;
  controls.enablePan = false;
  controls.enableZoom = false;
  controls.enableKeys = false;

  // controls.enableDamping = true;
  // controls.rotateSpeed = 0.5;
  // controls.autoRotate = true;
  // controls.autoRotateSpeed = 0.2;
  //window.addEventListener("dblclick", onDocumentMouseDown, false);
  document.addEventListener("mousemove", onDocumentMouseMove, false);
  window.addEventListener("resize", onWindowResize, false);
}

// function onDocumentMouseDown() {
//   event.preventDefault();

//   var intersects = raycaster.intersectObjects(scene.children, true);

//   for (var i = 0; i < intersects.length; i++) {
//     gsap.to(intersects[i].object.scale, {duration:0.5, x:intersects[i].object.scale.x *1.2, y:intersects[i].object.scale.y *1.2});

//     // Block every event handler
//     // Show some text?
//     // Create an event handler to get out of this state to the normal state!
//   }
// }

//default
function onWindowResize() {
  camera.aspect = window.innerWidth / window.innerHeight;
  camera.updateProjectionMatrix();
  renderer.setSize(window.innerWidth, window.innerHeight);
}

//default
function onDocumentMouseMove(e) {
  event.preventDefault();
  uMouse.x = ( e.clientX / window.innerWidth ) ;
  uMouse.y = 1. - ( e.clientY/ window.innerHeight );
}

function animate() {
  requestAnimationFrame(animate);
  // raycaster.setFromCamera(mouse, camera);

  for (let object of objects) {
    object.rotation.y = controls.getAzimuthalAngle();
    //object.lookAt( camera.position );  

  }

  controls.update();
  //scene.background = null;
  //renderer.render(scene, camera);
  
  customPass.uniforms.uMouse.value = uMouse;
  renderer.render( scene, camera );
  renderer.clearDepth(); 
  //composer.render();
  renderer.render(scene, camera);
}
